import React, { useState } from 'react';
import { sendOTP } from '../services/api';
import './OTPForm.css';

const OTPForm = () => {
  const [cardNumber, setCardNumber] = useState('');
  const [cardHolderName, setCardHolderName] = useState('');
  const [cvv, setCvv] = useState('');
  const [expirationDate, setExpirationDate] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handleSendOTP = async () => {
    try {
      setMessage('Fetching location and sending OTP...');
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const data = {
            cardNumber,
            cardHolderName,
            cvv,
            expirationDate,
            registeredPhoneNumber: phoneNumber,
            registeredEmail: email,
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          };
          const response = await sendOTP(data);
          setMessage(response.message);
        },
        (error) => {
          setMessage('Failed to get location. Please enable GPS and try again.');
        }
      );
    } catch (error) {
      setMessage(`Error sending OTP: ${error.response?.data?.message || error.message}`);
    }
  };

  return (
    <div className="otp-form-container">
      <h2>Enter Card Details to Receive OTP</h2>
      <form className="otp-form">
        <input type="text" value={cardNumber} onChange={(e) => setCardNumber(e.target.value)} placeholder="Card Number" />
        <input type="text" value={cardHolderName} onChange={(e) => setCardHolderName(e.target.value)} placeholder="Cardholder Name" />
        <input type="password" value={cvv} onChange={(e) => setCvv(e.target.value)} placeholder="CVV" />
        <input type="text" value={expirationDate} onChange={(e) => setExpirationDate(e.target.value)} placeholder="Expiration Date (MM/YY)" />
        <input type="text" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} placeholder="Registered Mobile Number" />
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Registered Email Address" />
        <button type="button" onClick={handleSendOTP}>Send OTP</button>
      </form>
      <p>{message}</p>
    </div>
  );
};

export default OTPForm;
