import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { ThemeContext } from '../context/ThemeContext';
import axios from 'axios';
import './OTPPage.css';

const OTPPage = () => {
  const { theme } = useContext(ThemeContext);
  const { otp } = useParams(); // Extract OTP from the URL
  const [message, setMessage] = useState('');

  console.log('OTP extracted from URL:', otp); // Debugging log

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;
        console.log('Device location fetched:', { latitude, longitude }); // Debugging log

        try {
          // Send OTP and device location to backend
          const response = await axios.post('http://localhost:5000/api/users/verifyTransaction', {
            otp, // Send the OTP extracted from the URL
            latitude,
            longitude,
          });
          setMessage(response.data.message);
        } catch (error) {
          console.error('Error verifying transaction:', error); // Debugging log
          setMessage(error.response?.data?.message || 'Error verifying transaction');
        }
      },
      (error) => {
        console.error('Geolocation error:', error); // Debugging log
        setMessage('Failed to get location. Please enable GPS and try again.');
      }
    );
  }, [otp]);

  return (
    <div className={`otp-page-container ${theme}`}>
      <h2>Your OTP</h2>
      <p>{message}</p>
    </div>
  );
};

export default OTPPage;
