import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import OTPForm from './components/OTPForm';
import OTPVerification from './components/OTPVerification';
import OtpPage from './components/OPage';
import './App.css';

function App() {
  return (
    <Router>
      <div className="App">
        <h1>OTP Location-Based Verification</h1>
        <Routes>
          <Route path="/" element={
            <div className="form">
              <OTPForm />
              <OTPVerification />
            </div>
          } />
          <Route path="/otp/:token" element={<OtpPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
