  const handleVerifyTransaction = async () => {
    try {
      console.log('Sending OTP and location to backend:', { otp, latitude, longitude }); // Debugging log
      const response = await axios.post('http://localhost:5000/api/users/verifyTransaction', {
        otp,
        latitude,
        longitude,
      });
      console.log('Backend response:', response.data); // Debugging log
      setMessage(response.data.message);
    } catch (error) {
      console.error('Error verifying transaction:', error); // Debugging log
      setMessage(error.response?.data?.message || 'Error verifying transaction');
    }
  };

  return (
    <div className={`otp-verification-container ${theme}`}>
      <h2>Verify Your Transaction</h2>
      <div className="otp-layout">
        <div className="card-image">
          <img src="https://www.pngplay.com/wp-content/uploads/7/Debit-Card-Transparent-Image.png" alt="Virtual Debit Card" />
        </div>
        <div className="otp-form">
          <input
            type="text"
            value={cardNumber}
            onChange={(e) => setCardNumber(e.target.value)}
            placeholder="Card Number"
            className="form-control"
          />
          <input
            type="text"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
            placeholder="Enter OTP"
            className="form-control"
          />
          <button type="button" onClick={handleVerifyTransaction} className="btn btn-primary">
            Verify Transaction
          </button>
        </div>
      </div>
      {message && (
        <div className={`alert alert-${message.includes('success') ? 'success' : 'danger'} mt-3`}>
          {message}
        </div>
      )}
    </div>
  );
};

export default OTPVerification;
