import React, { useState, useEffect } from 'react';
import { verifyTransaction } from '../services/api';
import { useLocation, useNavigate } from 'react-router-dom';
import './OTPVerification.css';

const OTPVerification = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const queryParams = new URLSearchParams(location.search);
  const otpFromLink = queryParams.get('otp');
  const cardNumberFromLink = queryParams.get('cardNumber');

  const [cardNumber, setCardNumber] = useState(cardNumberFromLink || '');
  const [otp, setOtp] = useState(otpFromLink || '');
  const [message, setMessage] = useState('');
  const [locationFetched, setLocationFetched] = useState(false);

  useEffect(() => {
    if (!otpFromLink || !cardNumberFromLink) {
      navigate('/'); // Redirect to home if OTP or card number is missing
    } else {
      fetchLocation();
    }
  }, [otpFromLink, cardNumberFromLink, navigate]);

  const fetchLocation = () => {
    navigator.geolocation.getCurrentPosition(
      () => {
        setLocationFetched(true);
      },
      (error) => {
        setMessage('Failed to get location. Please enable GPS and try again.');
      }
    );
  };

  const handleVerifyTransaction = async () => {
    try {
      setMessage('Verifying transaction...');
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const data = {
            cardNumber,
            otp,
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          };
          const response = await verifyTransaction(data);
          setMessage(response.message);
        },
        (error) => {
          setMessage('Failed to get location. Please enable GPS and try again.');
        }
      );
    } catch (error) {
      setMessage(`Error verifying transaction: ${error.response?.data?.message || error.message}`);
    }
  };

  return (
    <div className="otp-verification-container">
      <h2>Verify Your Transaction</h2>
      <div className="otp-layout">
        <div className="card-image">
          <img src="https://www.pngplay.com/wp-content/uploads/7/Debit-Card-Transparent-Image.png" alt="Virtual Debit Card" />
        </div>
        <div className="otp-form">
          <input type="text" value={cardNumber} onChange={(e) => setCardNumber(e.target.value)} placeholder="Card Number" />
          {locationFetched ? (
            <input type="text" value={otp} onChange={(e) => setOtp(e.target.value)} placeholder="Enter OTP" />
          ) : (
            <p>Fetching location...</p>
          )}
          <button type="button" onClick={handleVerifyTransaction}>Verify Transaction</button>
          <p>{message}</p>
        </div>
      </div>
    </div>
  );
};

export default OTPVerification;
