///^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^6

import React, { useState } from 'react';
import { verifyTransaction } from '../services/api';
import './OTPVerification.css';

const OTPVerification = () => {
  const [cardNumber, setCardNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [message, setMessage] = useState('');

  // const handleVerifyTransaction = async () => {
  //   try {
  //     setMessage('Verifying transaction...');
  //     const data = { cardNumber, otp };  // Create an object with cardNumber and otp
  //     const response = await verifyTransaction(data);  // Pass the object as expected
  //     setMessage(response.message);  // Use response.message for the result
  //   } catch (error) {
  //     setMessage(`Error verifying transaction: ${error.response?.data?.message || error.message}`);
  //     console.error('Error details:', error.response?.data || error.message);
  //   }
  // };

  return (
    <div className="otp-verification-container">
      <h2>Verify Your Transaction</h2>
      <div className="otp-layout">
        <div className="card-image">
          <img
            src="https://www.pngplay.com/wp-content/uploads/7/Debit-Card-Transparent-Image.png"
            alt="Virtual Debit Card"
          />
        </div>
        <div className="otp-form">
          <input
            type="text"
            value={cardNumber}
            onChange={(e) => setCardNumber(e.target.value)}
            placeholder="Card Number"
          />
          <input
            type="text"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
            placeholder="Enter OTP"
          />
          <button type="button" onClick={handleVerifyTransaction}>Verify Transaction</button>
          <p>{message}</p>
        </div>
      </div>
    </div>
  );
};

export default OTPVerification;
