import React, { useState, useContext } from 'react';
import { sendOTP } from '../services/api';
import { useNavigate } from 'react-router-dom';
import { ThemeContext } from '../context/ThemeContext';
import './OTPForm.css';

const OTPForm = () => {
  const { theme, toggleTheme } = useContext(ThemeContext);
  const navigate = useNavigate();
  const [cardNumber, setCardNumber] = useState('');
  const [cardHolderName, setCardHolderName] = useState('');
  const [cvv, setCvv] = useState('');
  const [expirationDate, setExpirationDate] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handleSendOTP = async () => {
    try {
      setMessage('Fetching location and sending OTP...');
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const data = {
            cardNumber,
            cardHolderName,
            cvv,
            expirationDate,
            registeredPhoneNumber: phoneNumber,
            registeredEmail: email,
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          };
          const response = await sendOTP(data);
          setMessage(response.message);
        },
        (error) => {
          setMessage('Failed to get location. Please enable GPS and try again.');
        }
      );
    } catch (error) {
      navigate('/error', { state: { error: error.response?.data?.message || error.message } });
    }
  };

  return (
    <div className={`otp-form-container ${theme}`}>
      <button onClick={toggleTheme} className="btn btn-secondary">
        Toggle {theme === 'light' ? 'Dark' : 'Light'} Mode
      </button>
      <h2>Enter Card Details to Receive OTP</h2>
      <form className="otp-form">
        <input type="text" value={cardNumber} onChange={(e) => setCardNumber(e.target.value)} placeholder="Card Number" className="form-control" />
        <input type="text" value={cardHolderName} onChange={(e) => setCardHolderName(e.target.value)} placeholder="Cardholder Name" className="form-control" />
        <input type="password" value={cvv} onChange={(e) => setCvv(e.target.value)} placeholder="CVV" className="form-control" />
        <input type="text" value={expirationDate} onChange={(e) => setExpirationDate(e.target.value)} placeholder="Expiration Date (MM/YY)" className="form-control" />
        <input type="text" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} placeholder="Registered Mobile Number" className="form-control" />
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Registered Email Address" className="form-control" />
        <button type="button" onClick={handleSendOTP} className="btn btn-primary">Send OTP</button>
      </form>
      {message && <div className={`alert alert-${message.includes('success') ? 'success' : 'danger'} mt-3`}>{message}</div>}
    </div>
  );
};

export default OTPForm;
