import React, { useState } from 'react';
import { verifyTransaction } from '../services/api';

const OTPVerification = () => {
  const [cardNumber, setCardNumber] = useState('');
  const [cardHolderName, setCardHolderName] = useState('');
  const [cvv, setCvv] = useState('');
  const [expirationDate, setExpirationDate] = useState('');
  const [otp, setOtp] = useState('');
  const [message, setMessage] = useState('');

  const handleVerifyTransaction = async () => {
    try {
      setMessage('Verifying transaction...');
      const data = await verifyTransaction({ cardNumber, cardHolderName, cvv, expirationDate, otp });
      setMessage(data.message);  // Display the success or error message from backend
    } catch (error) {
      setMessage('Error verifying transaction');
    }
  };

  return (
    <div>
      <h2>Enter Details to Verify Transaction</h2>
      <input
        type="text"
        value={cardNumber}
        onChange={(e) => setCardNumber(e.target.value)}
        placeholder="Card Number"
      />
      <input
        type="text"
        value={cardHolderName}
        onChange={(e) => setCardHolderName(e.target.value)}
        placeholder="Cardholder Name"
      />
      <input
        type="text"
        value={cvv}
        onChange={(e) => setCvv(e.target.value)}
        placeholder="CVV"
      />
      <input
        type="text"
        value={expirationDate}
        onChange={(e) => setExpirationDate(e.target.value)}
        placeholder="Expiration Date (MM/YY)"
      />
      <input
        type="text"
        value={otp}
        onChange={(e) => setOtp(e.target.value)}
        placeholder="Enter OTP"
      />
      <button onClick={handleVerifyTransaction}>Verify Transaction</button>
      <p>{message}</p>
    </div>
  );
};

export default OTPVerification;
