import React, { useState } from 'react';
import { verifyTransaction } from '../services/api';
import './OTPVerification.css'; // Styling for layout

const OTPVerification = () => {
  const [otp, setOtp] = useState('');
  const [message, setMessage] = useState('');

  const handleVerifyTransaction = async () => {
    try {
      setMessage('Verifying transaction...');
      const data = await verifyTransaction({ otp });
      setMessage(data.message);
    } catch (error) {
      setMessage('Error verifying transaction');
    }
  };

  return (
    <div className="otp-verification-container">
      <h2>Verify Your Transaction</h2>
      <div className="otp-layout">
        {/* Virtual Card Image */}
        <div className="card-image">
          <img
            src="https://via.placeholder.com/350x200.png?text=Virtual+Debit+Card"
            alt="Virtual Debit Card"
          />
        </div>
        {/* OTP Form */}
        <div className="otp-form">
          <input
            type="text"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
            placeholder="Enter OTP"
          />
          <button type="button" onClick={handleVerifyTransaction}>Verify Transaction</button>
          <p>{message}</p>
        </div>
      </div>
    </div>
  );
};

export default OTPVerification;




