import React, { useState } from 'react';
import { verifyTransaction } from '../services/api';

const OTPVerification = () => {
  const [cardNumber, setCardNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [message, setMessage] = useState('');

  const handleVerifyTransaction = async () => {
    try {
      setMessage('Verifying transaction...');
      const data = await verifyTransaction(cardNumber, otp);
      setMessage(data.message);  // Show success or error message from backend
    } catch (error) {
      setMessage('Error verifying transaction');
    }
  };

  return (
    <div>
      <h2>Enter OTP to Verify Transaction</h2>
      <input
        type="text"
        value={cardNumber}
        onChange={(e) => setCardNumber(e.target.value)}
        placeholder="Enter your card number"
      />
      <input
        type="text"
        value={otp}
        onChange={(e) => setOtp(e.target.value)}
        placeholder="Enter the OTP"
      />
      <button onClick={handleVerifyTransaction}>Verify Transaction</button>
      <p>{message}</p>
    </div>
  );
};

export default OTPVerification;
