// 5555555555%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
// process.env.NODE_TLS_REJECT_UNAUTHORIZED = '0';

import axios from 'axios';

const API_URL = process.env.REACT_APP_BACKEND_URL || 'http://192.168.0.114:5000/api/users';

export const sendOTP = async (data) => {
  try {
    console.log(`Request URL: ${API_URL}/sendOTP`);

    const response = await axios.post(`${API_URL}/sendOTP`, data, { withCredentials: true });
    return response.data;
  } catch (error) {
    console.error('Error sending OTP:', error.response?.data || error.message);
    throw error;
  }
};

export const verifyTransaction = async (data) => {
  try {
    const response = await axios.post(`${API_URL}/verifyTransaction`, data, { withCredentials: true });
    return response.data;
  } catch (error) {
    console.error('Error verifying transaction:', error.response?.data || error.message);
    throw error;
  }
};
