// new code

import axios from 'axios';

// Set the base URL for your backend API
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api/users';

// Function to send OTP
export const sendOTP = async (cardNumber) => {
  try {
    const response = await axios.post(`${API_URL}/sendOTP`, { cardNumber });
    return response.data;  // Return the response data
  } catch (error) {
    console.error('Error sending OTP:', error);
    throw error;
  }
};

// Function to verify the transaction
export const verifyTransaction = async (cardNumber, otp) => {
  try {
    const response = await axios.post(`${API_URL}/verifyTransaction`, { cardNumber, otp });
    return response.data;  // Return the response data
  } catch (error) {
    console.error('Error verifying transaction:', error);
    throw error;
  }
};
